import React, { useState, useEffect} from 'react'
import { ethers } from "ethers"
import { Form } from 'react-bootstrap'
import { create as ipfsHttpClient } from 'ipfs-http-client'
import { Row, Col, Card, Button } from 'react-bootstrap'



const Generate = ({ marketplace, nft, }) => {
  const [count, setCount] = useState(null)


  const generateNFT = async () => {
    if (!count) return

    const uri = `http://127.0.0.1:5000/generator/${count}`
    fetch(uri)
    .then(response => response.json())
    .then(data => {
      loadNFTImages(data);
    });
  }

  const loadNFTImages = async (data) => { 
    const [items, setItems] = useState(null)

    console.log(data.items);
    items = data;
    console.log(items.length)

    setItems(items)
  }

  const listNFTImage = async (item) => {
    //await (await marketplace.purchaseItem(item.itemId, { value: item.totalPrice })).wait()
    //loadMarketplaceItems()
  }

  return (
    <div className="container-fluid mt-5">
      <div className="row">
        <main role="main" className="col-lg-12 mx-auto" style={{ maxWidth: '1000px' }}>
          <div className="content mx-auto">
            <Row className="g-4">
              <Form.Control onChange={(e) => setCount(e.target.value)} size="lg" required type="number" placeholder="Number of NFT Images" />
              <div className="d-grid px-0">
                <Button onClick={generateNFT} variant="primary" size="lg">
                  Generate!
                </Button>
              </div>
            </Row>
          </div>
        </main>

        <div className="flex justify-center">
        {items.length > 0 ?
          <div className="px-5 container">
            <Row xs={1} md={2} lg={4} className="g-4 py-5">
              {items.map((item, idx) => (
                <Col key={idx} className="overflow-hidden">
                  <Card>
                    <Card.Img variant="top" src={item.address} />
                    <Card.Body color="secondary">
                      <Card.Title>{item.name}</Card.Title>
                    </Card.Body>
                    <Card.Footer>
                      <div className='d-grid'>
                        <Button onClick={() => listNFTImage(item)} variant="primary" size="lg">
                          List!
                        </Button>
                      </div>
                    </Card.Footer>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
          : (
            <main style={{ padding: "1rem 0" }}>
              <h2>No listed NFT Images</h2>
            </main>
          )}
      </div>
      </div>
    </div>
  );
}

export default Generate
